import React, { useState } from 'react';
import '../styles/Card.scss';
import useClickOutside from '../components/useClickOutside'

const ToDoList = () => {
  const [tasks, setTasks] = useState(
    [
        {
            type: 'Fruit',
            name: 'Apple',
        },
        {
            type: 'Vegetable',
            name: 'Broccoli',
        },
        {
            type: 'Vegetable',
            name: 'Mushroom',
        },
        {
            type: 'Fruit',
            name: 'Banana',
        },
        {
            type: 'Vegetable',
            name: 'Tomato',
        },
        {
            type: 'Fruit',
            name: 'Orange',
        },
        {
            type: 'Fruit',
            name: 'Mango',
        },
        {
            type: 'Fruit',
            name: 'Pineapple',
        },
        {
            type: 'Vegetable',
            name: 'Cucumber',
        },
        {
            type: 'Fruit',
            name: 'Watermelon',
        },
        {
            type: 'Vegetable',
            name: 'Carrot',
        },
    ]
  );
  const [fruits, setFruits] = useState([]);
  const [vegetables, setVegetables] = useState([]);

  const updatedTask = (item) => {
    const updatedTasks = tasks.filter((task, i) => task.name !== item.name);
    setTasks(updatedTasks);

    if (item.type === 'Fruit') {
        setFruits([...fruits, item]);
    }else{
        setVegetables([...vegetables, item]);
    }
  };

  const removedFruit = (newFruit) => {
    const updatedFruit = fruits.filter((item, i) => item.name !== newFruit.name);
    setFruits(updatedFruit);
    
    setTasks([...tasks, newFruit]);
  }

  const removedVeg = (newVegetable) => {
    const updatedTasksVeg = vegetables.filter((task, i) => task.name !== newVegetable.name);
    setVegetables(updatedTasksVeg);

    setTasks([...tasks, newVegetable]);
  }

  const reverseTask = (items) => {
    let todo = [...items]
    setTasks(todo)
  }

  const ref = useClickOutside(handleHeaderClick);
  const handleHeaderClick = (event) => {
    // do something

    event.stopPropagation();
  };

  return (
    <div className="tasks-grid">
        <div className="todo-list">
            <ul>
                {tasks.map((task, index) => (
                <li key={index} className="task-card" onClick={() => updatedTask(task)}>
                    <span>{task.name}</span>
                </li>
                ))}
            </ul>
        </div>
        <div className="todo-list" onClick={handleHeaderClick}>
            <h1>Fruit</h1>
            <ul>
                {fruits.map((task, index) => (
                <li key={index} className="task-card" ref={ref} onClick={() => removedFruit(task)}>
                    <span>{task.name}</span>
                </li>
                ))}
            </ul>
        </div>
        <div className="todo-list">
            <h1>Vegetable</h1>
            <ul>
                {vegetables.map((task, index) => (
                <li key={index} className="task-card" onClick={() => removedVeg(task)}>
                    <span>{task.name}</span>
                </li>
                ))}
            </ul>
        </div>
    </div>
  );
};

export default ToDoList;
