import React, { useEffect, useState } from "react";
import "../styles/Card.scss";

const ToDoList = () => {
  const [lsAdd, setAddList] = useState([]);
  const [sessionTime, setTime] = useState([]);    
  const [fruits, setFruits] = useState([]);
  const [vegetables, setVegetables] = useState([]);
  const [tasks, setTasks] = useState([
    {
      type: "Fruit",
      name: "Apple",
    },
    {
      type: "Vegetable",
      name: "Broccoli",
    },
    {
      type: "Vegetable",
      name: "Mushroom",
    },
    {
      type: "Fruit",
      name: "Banana",
    },
    {
      type: "Vegetable",
      name: "Tomato",
    },
    {
      type: "Fruit",
      name: "Orange",
    },
    {
      type: "Fruit",
      name: "Mango",
    },
    {
      type: "Fruit",
      name: "Pineapple",
    },
    {
      type: "Vegetable",
      name: "Cucumber",
    },
    {
      type: "Fruit",
      name: "Watermelon",
    },
    {
      type: "Vegetable",
      name: "Carrot",
    },
  ]);

  //Session timeout 5 sec
  useEffect(() => {

    console.log(sessionTime);
    console.log(lsAdd);
    let timeOutIndex = sessionTime.indexOf(0);
    while (timeOutIndex !== -1) {
        lsAdd.splice(timeOutIndex,1);
        sessionTime.splice(timeOutIndex,1);
        timeOutIndex = sessionTime.indexOf(0, timeOutIndex + 1);
    }

    const countdownInterval = setInterval(() => {
        const nextCounters = counters.map((c, i) => {
            if (i === index) {
              // Increment the clicked counter
              return c + 1;
            } else {
              // The rest haven't changed
              return c;
            }
          });
          setCounters(nextCounters);  
    }, 1000);

    return () => clearInterval(countdownInterval);
  }, [lsAdd, sessionTime]);

  //Add task
  const updatedTask = (item) => {
    const updatedTasks = tasks.filter((task, i) => task.name !== item.name);
    setTasks(updatedTasks);

    if (item.type === "Fruit") {
      setFruits([...fruits, item]);
    } else {
      setVegetables([...vegetables, item]);
    }

    setTime([...sessionTime, 5]);
    setAddList([...lsAdd, item]);
  };

  // Removed function
  const removed = (item) =>{
    if (item.type === "Fruit") {
        removedFruit(item);
    }else{
        removedVeg(item);
    }

    lsAdd.forEach((add, i) => {
        if (add.name === item.name) {
            sessionTime.splice(i, 1);
            lsAdd.splice(i, 1);
        }
    })
  }

  //sub func removed
  const removedFruit = (newFruit) => {
    const updatedFruit = fruits.filter(
      (item, i) => item.name !== newFruit.name
    );
    setFruits(updatedFruit);

    setTasks([...tasks, newFruit]);
  };

  //sub func removed
  const removedVeg = (newVegetable) => {
    const updatedTasksVeg = vegetables.filter(
      (task, i) => task.name !== newVegetable.name
    );
    setVegetables(updatedTasksVeg);

    setTasks([...tasks, newVegetable]);
  };

  //Reverse value back to main list
  const reverseTask = (type, item, index) => {
    console.log(item);
    if (item !== undefined) {
      if (type === "Fruit" && fruits.length !== 0) {
        let todo = [...fruits];
        todo.splice(index, 1);
        setFruits(todo);
      } else if (type === "Vegetable" && vegetables.length !== 0) {
        let todo = [...vegetables];
        todo.splice(index, 1);
        setVegetables(todo);
      }
      setTasks([...tasks, item]);
    }
  };

  return (
    <div className="tasks-grid">
      <div className="todo-list">
        <ul>
          {tasks.map((task, index) => (
            <li
              key={index}
              className="task-card"
              onClick={() => updatedTask(task)}
            >
              <span>{task.name}</span>
            </li>
          ))}
        </ul>
      </div>
      <div className="todo-list">
        <h1>Fruit</h1>
        <ul>
          {fruits.map((task, index) => (
            <li
              key={index}
              className="task-card"
              onClick={() => removed(task)}
            >
              <span>{task.name}</span>
            </li>
          ))}
        </ul>
        <div
          onClick={() =>
            reverseTask("Fruit", fruits[fruits.length - 1], fruits.length - 1)
          }
        ></div>
      </div>
      <div className="todo-list">
        <h1>Vegetable</h1>
        <ul>
          {vegetables.map((task, index) => (
            <li
              key={index}
              className="task-card"
              onClick={() => removed(task)}
            >
              <span>{task.name}</span>
            </li>
          ))}
        </ul>
        <div
          onClick={() =>
            reverseTask(
              "Vegetable",
              vegetables[vegetables.length - 1],
              vegetables.length - 1
            )
          }
        ></div>
      </div>
    </div>
  );
};

export default ToDoList;
