import React, { useState } from 'react';
import '../styles/Card.scss';

const ToDoList = () => {
  const [tasks, setTasks] = useState(
    [
        {
            type: 'Fruit',
            name: 'Apple',
        },
        {
            type: 'Vegetable',
            name: 'Broccoli',
        },
        {
            type: 'Vegetable',
            name: 'Mushroom',
        },
        {
            type: 'Fruit',
            name: 'Banana',
        },
        {
            type: 'Vegetable',
            name: 'Tomato',
        },
        {
            type: 'Fruit',
            name: 'Orange',
        },
        {
            type: 'Fruit',
            name: 'Mango',
        },
        {
            type: 'Fruit',
            name: 'Pineapple',
        },
        {
            type: 'Vegetable',
            name: 'Cucumber',
        },
        {
            type: 'Fruit',
            name: 'Watermelon',
        },
        {
            type: 'Vegetable',
            name: 'Carrot',
        },
    ]
  );
  const [fruits, setFruits] = useState([]);
  const [vegetables, setVegetable] = useState([]);

  const updatedTask = (item) => {
    const updatedTasks = tasks.filter((task, i) => task.name !== item.name);
    setTasks(updatedTasks);

    if (item.type === 'Fruit') {
        setFruits([...fruits, item]);
    }else{
        setVegetable([...vegetables], item);
    }
  };

  const addFruit = (newFruit) => {
    const updatedTasksFruit = tasks.filter((task, i) => task.name !== newFruit);
    setTasks(updatedTasksFruit);

    setFruits([...fruits], newFruit);
  }

  const addVegetable = (newVegetable) => {
    const updatedTasksVeg = tasks.filter((task, i) => task.name !== newVegetable);
    setTasks(updatedTasksVeg);

    setVegetable([...vegetables], newVegetable);
  }

  const deleteTask = (index) => {
    const updatedTasks = tasks.filter((task, i) => i !== index);
    setTasks(updatedTasks);
  };

  return (
    <div className="tasks-grid">
        <div className="todo-list">
            <ul>
                {tasks.map((task, index) => (
                <li key={index} className="task-card" onClick={() => updatedTask(task)}>
                    <span>{task.name}</span>
                </li>
                ))}
            </ul>
        </div>
        <div className="todo-list">
            <h1>Fruit</h1>
            <ul>
                {fruits.map((task, index) => (
                <li key={index} className="task-card">
                    <span>{task.name}</span>
                    <button onClick={() => deleteTask(index)}>Delete</button>
                </li>
                ))}
            </ul>
        </div>
        <div className="todo-list">
            <h1>Vegetable</h1>
            <ul>
                {vegetables.map((task, index) => (
                <li key={index} className="task-card">
                    <span>{task.name}</span>
                    <button onClick={() => deleteTask(index)}>Delete</button>
                </li>
                ))}
            </ul>
        </div>
    </div>
  );
};

export default ToDoList;
